import _ from 'lodash'
import React from 'react'
import Tone from 'tone'

import PlaySwitch from '../components/PlaySwitch'
import Distortion from '../components/effects/Distortion'
import FeedbackDelay from '../components/effects/FeedbackDelay'

export default class Synth extends React.Component {
  constructor(props) {
    super(props)

    let autoFilter = new Tone.AutoFilter({
      frequency: 1,
      type: 'sine',
      depth: 1,
      baseFrequency: 200,
      octaves: 2.6,
      filter: {
        type: 'lowpass',
        rolloff: -12,
        Q: 1
      }
    })

    let autoPanner = new Tone.AutoPanner({
      frequency: 15,
      type: 'sine',
      depth: 1
    })

    let autoWah = new Tone.AutoWah({
      baseFrequency: 100,
      octaves: 6,
      sensitivity: 0,
      Q: 2,
      gain: 2,
      follower: {
        attack: 0.3,
        release: 0.5
      }
    })

    let bitCrusher = new Tone.BitCrusher({
      bits: 4
    })

    let chebyshev = new Tone.Chebyshev({
      order: 50,
      oversample: 'none'
    })

    let chorus = new Tone.Chorus({
      frequency: 1.5,
      delayTime: 3.5,
      depth: 0.7,
      type: 'sine',
      spread: 180
    })

    let convolver = new Tone.Convolver({
      onload: Tone.noOp,
      normalize: true
    })

    let distortion = new Tone.Distortion({
      // distortion: 0.4,
      // oversample: 'none'
      distortion: 0,
      oversample: '4x'
    })

    // let effect = new Tone.Effect({
    //   wet: 1
    // }).toMaster()

    let feedbackDelay = new Tone.FeedbackDelay({
      delayTime: '4n',
      maxDelay: 0.8
    })

    let feedbackEffect = new Tone.FeedbackEffect({
      feedback: 0.125
    })

    let freeverb = new Tone.Freeverb({
      roomSize: 0.7,
      dampening: 3000
    })

    let jcReverb = new Tone.JCReverb({
      roomSize: 0.5
    })

    // MidSideEffect ???

    let phaser = new Tone.Phaser({
      frequency: 0.5,
      octaves: 3,
      stages: 10,
      Q: 10,
      baseFrequency: 350
    })

    let pingPongDelay = new Tone.PingPongDelay({
      delayTime: 0.25,
      maxDelayTime: 1
    })

    let pitchShift = new Tone.PitchShift({
      pitch: 0,
      windowSize: 0.1,
      delayTime: 0,
      feedback: 0
    })

    let reverb = new Tone.Reverb({
      decay: 1.5,
      preDelay: 0.01
    })

    // StereoEffect ???
    // StereoFeedbackEffect ???

    let stereoWidener = new Tone.StereoWidener({
      width: 0.5
    })

    // StereoXFeedbackEffect ???

    let tremolo = new Tone.Tremolo({
      frequency: 10,
      type: 'sine',
      depth: 0,
      spread: 360
    })

    let vibrato = new Tone.Vibrato({
      maxDelay: 0.005,
      frequency: 5,
      depth: 0.1,
      type: 'sine'
    })

    autoFilter.wet.value = 0
    autoPanner.wet.value = 0
    autoWah.wet.value = 0
    bitCrusher.wet.value = 0
    chebyshev.wet.value = 0
    chorus.wet.value = 0
    convolver.wet.value = 0
    distortion.wet.value = 0
    feedbackDelay.wet.value = 0
    feedbackEffect.wet.value = 0
    freeverb.wet.value = 0
    jcReverb.wet.value = 0
    phaser.wet.value = 0
    pingPongDelay.wet.value = 0
    pitchShift.wet.value = 0
    reverb.wet.value = 0
    stereoWidener.wet.value = 0
    tremolo.wet.value = 0
    vibrato.wet.value = 0

    let synth = new Tone.Synth()

    synth.chain(
      autoFilter,
      autoPanner,
      autoWah,
      bitCrusher,
      chebyshev,
      chorus,
      convolver,
      distortion,
      feedbackDelay,
      feedbackEffect,
      freeverb,
      jcReverb,
      phaser,
      pingPongDelay,
      pitchShift,
      reverb,
      stereoWidener,
      tremolo,
      vibrato,
      Tone.Master
    )

    let loop = new Tone.Loop(function(time) {
      synth.triggerAttackRelease('C2', '8n', time)
    }, '4n')

    this.state = {
      autoFilter: {
        effect: autoFilter,
        wet: 0,
        on: false
      },
      autoPanner: {
        effect: autoPanner,
        wet: 0,
        on: false
      },
      autoWah: {
        effect: autoWah,
        wet: 0,
        on: false
      },
      bitCrusher: {
        effect: bitCrusher,
        wet: 0,
        on: false
      },
      chebyshev: {
        effect: chebyshev,
        wet: 0,
        on: false
      },
      chorus: {
        effect: chorus,
        wet: 0,
        on: false
      },
      convolver: { convolver },
      distortion: {
        effect: distortion,
        wet: 0,
        on: false
      },
      feedbackDelay: {
        effect: feedbackDelay,
        wet: 0,
        on: false
      },
      feedbackEffect: {
        effect: feedbackEffect,
        wet: 0,
        on: false
      },
      freeverb: {
        effect: freeverb,
        wet: 0,
        on: false
      },
      jcReverb: {
        effect: jcReverb,
        wet: 0,
        on: false
      },
      phaser: {
        effect: phaser,
        wet: 0,
        on: false
      },
      pingPongDelay: { pingPongDelay },
      pitchShift: {
        effect: pitchShift,
        wet: 0,
        on: false
      },
      reverb: {
        effect: reverb,
        wet: 0,
        on: false
      },
      stereoWidener: {
        effect: stereoWidener,
        wet: 0,
        on: false
      },
      tremolo: {
        effect: tremolo,
        wet: 0,
        on: false
      },
      vibrato: {
        effect: vibrato,
        wet: 0,
        on: false
      },
      synth: {
        instrument: synth,
        on: false
      },
      loop
    }

    _.bindAll(
      this,
      'toggleSynth',
      // 'toggleDistortion',
      'changeEffectWetValue',
      'changeDistortionValue',
      'toggleTremolo',
      'changeTremoloWetValue',
      'changeTremoloValue',
      'toggleAutoPanner',
      'changeAutoPannerWetValue',
      'changeAutoPannerValue',
      'togglEffect',
      'changeFeedbackDelayValue'
    )
  }

  toggleSynth() {
    let { synth, loop } = this.state
    let { instrument, on } = synth

    // let synth = new Tone.Synth() //.toMaster()
    // synth.chain(this.state.distortion, Tone.Master)
    //
    // this.setState({
    //   synth: synth
    // })

    on == true ? loop.stop() : loop.start('0m')

    this.setState({
      synth: {
        instrument: instrument,
        on: !on
      }
    })

    Tone.Transport.bpm.value = 115
    Tone.Transport.start()
  }

  togglEffect(effectName) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? 0 : wet
    on = !on

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  toggleTremolo() {
    let { effect, wet, on } = this.state.tremolo

    effect.wet.value = on == true ? 0 : wet
    on = !on

    this.setState({
      tremolo: {
        effect,
        wet,
        on
      }
    })
  }

  toggleAutoPanner() {
    let { effect, wet, on } = this.state.autoPanner

    effect.wet.value = on == true ? 0 : wet
    on = !on

    this.setState({
      autoPanner: {
        effect,
        wet,
        on
      }
    })
  }

  changeEffectWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? value : 0

    wet = value
    // console.log('value', effect.wet.value)

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeTremoloWetValue(value) {
    let { effect, wet, on } = this.state.tremolo

    effect.wet.value = on == true ? value : 0

    wet = value

    this.setState({
      tremolo: {
        effect,
        wet,
        on
      }
    })
  }

  changeAutoPannerWetValue(value) {
    let { effect, wet, on } = this.state.autoPanner

    effect.wet.value = on == true ? value : 0

    wet = value

    this.setState({
      autoPanner: {
        effect,
        wet,
        on
      }
    })
  }

  changeDistortionValue(effectName, value) {
    let { effect, wet, on } = this.state.distortion

    effect.distortion = value

    this.setState({
      distortion: {
        effect,
        wet,
        on
      }
    })
  }

  changeFeedbackDelayValue(effectName, value) {
    let { effect, wet, on } = this.state.feedbackDelay

    effect.maxDelay = value

    this.setState({
      feedbackDelay: {
        effect,
        wet,
        on
      }
    })
  }

  changeTremoloValue(value) {
    let { effect, wet, on } = this.state.tremolo

    effect.depth = value
    // console.log('depth', depth)

    this.setState({
      tremolo: {
        effect,
        wet,
        on
      }
    })
  }

  changeAutoPannerValue(value) {
    let { effect, wet, on } = this.state.autoPanner

    effect.frequency = value

    this.setState({
      autoPanner: {
        effect,
        wet,
        on
      }
    })
  }

  render() {
    let { distortion, tremolo, synth, autoPanner } = this.state
    let { togglEffect } = this

    return (
      <div>
        Toggle synth
        <PlaySwitch
          name="play"
          value={synth.on}
          handleToggleClick={this.toggleSynth}
        />
        <Distortion
          {...this.state.distortion}
          togglEffect={() => togglEffect('distortion')}
          changeEffectWetValue={this.changeEffectWetValue}
          changeDistortionValue={this.changeDistortionValue}
        />
        <FeedbackDelay
          {...this.state.feedbackDelay}
          togglEffect={() => togglEffect('feedbackDelay')}
          changeEffectWetValue={this.changeEffectWetValue}
          changeFeedbackDelayValue={this.changeFeedbackDelayValue}
        />
      </div>
    )
  }
}
