import _ from 'lodash'
import React from 'react'
import Tone from 'tone'

import * as parts from '../tunes/parts'
import * as synths from '../tunes/synths'
// import * as sequence from '../sequence'

import PlaySwitch from '../components/PlaySwitch'
import Distortion from '../components/effects/Distortion'
import FeedbackDelay from '../components/effects/FeedbackDelay'
import BitCrusher from '../components/effects/BitCrusher'
import Reverb from '../components/effects/Reverb'
import Tremolo from '../components/effects/Tremolo'
import Freeverb from '../components/effects/Freeverb'
import JcReverb from '../components/effects/JcReverb'

import PolySynth from '../components/synths/PolySynth'
import ToneSynth from '../components/synths/ToneSynth'

export default class Synth extends React.Component {
  constructor(props) {
    super(props)

    console.log(props.wet)

    let ambientSynth = synths.toneSynth()

    let leadSynth = synths.polySynth()

    let autoFilter = new Tone.AutoFilter({
      frequency: 1,
      type: 'sine',
      depth: 1,
      baseFrequency: 200,
      octaves: 2.6,
      filter: {
        type: 'lowpass',
        rolloff: -12,
        Q: 1
      }
    })

    let autoPanner = new Tone.AutoPanner({
      frequency: 15,
      type: 'sine',
      depth: 1
    })

    let autoWah = new Tone.AutoWah({
      baseFrequency: 100,
      octaves: 6,
      sensitivity: 0,
      Q: 2,
      gain: 2,
      follower: {
        attack: 0.3,
        release: 0.5
      }
    })

    let bitCrusher = new Tone.BitCrusher({
      bits: 0
    })

    let chebyshev = new Tone.Chebyshev({
      order: 50,
      oversample: 'none'
    })

    let chorus = new Tone.Chorus({
      frequency: 1.5,
      delayTime: 3.5,
      depth: 0.7,
      type: 'sine',
      spread: 180
    })

    let convolver = new Tone.Convolver({
      onload: Tone.noOp,
      normalize: true
    })

    let distortion = new Tone.Distortion({
      // distortion: 0.4,
      // oversample: 'none'
      distortion: 0,
      oversample: '4x'
    })

    // let effect = new Tone.Effect({
    //   wet: 1
    // }).toMaster()

    let feedbackDelay = new Tone.FeedbackDelay({
      delayTime: '4n',
      maxDelay: 0.8
    })

    let feedbackEffect = new Tone.FeedbackEffect({
      feedback: 0.125
    })

    let freeverb = new Tone.Freeverb({
      roomSize: 0.7,
      dampening: 3000
    })

    let jcReverb = new Tone.JCReverb({
      roomSize: 0.5
    })

    let phaser = new Tone.Phaser({
      frequency: 0.5,
      octaves: 3,
      stages: 10,
      Q: 10,
      baseFrequency: 350
    })

    let pingPongDelay = new Tone.PingPongDelay({
      delayTime: 0.25,
      maxDelayTime: 1
    })

    let pitchShift = new Tone.PitchShift({
      pitch: 0,
      windowSize: 0.1,
      delayTime: 0,
      feedback: 0
    })

    let reverb = new Tone.Reverb({
      decay: 0,
      preDelay: 0
    })

    // StereoEffect ???
    // StereoFeedbackEffect ???

    let stereoWidener = new Tone.StereoWidener({
      width: 0.5
    })

    // StereoXFeedbackEffect ???

    let tremolo = new Tone.Tremolo({
      frequency: 10,
      type: 'sine',
      depth: 0,
      spread: 360
    })

    let vibrato = new Tone.Vibrato({
      maxDelay: 0.005,
      frequency: 5,
      depth: 0.1,
      type: 'sine'
    })

    autoFilter.wet.value = 0
    autoPanner.wet.value = 0
    autoWah.wet.value = 0
    bitCrusher.wet.value = 0
    chebyshev.wet.value = 0
    chorus.wet.value = 0
    convolver.wet.value = 0
    distortion.wet.value = 0
    feedbackDelay.wet.value = 0
    feedbackEffect.wet.value = 0
    freeverb.wet.value = 0
    jcReverb.wet.value = 0
    phaser.wet.value = 0
    pingPongDelay.wet.value = 0
    pitchShift.wet.value = 0
    reverb.wet.value = 0
    stereoWidener.wet.value = 0
    tremolo.wet.value = 0
    vibrato.wet.value = 0

    let synth = new Tone.PolySynth()
    let synth1 = new Tone.Synth()
    let synth2 = new Tone.Synth()
    let synth3 = new Tone.Synth()
    let synth4 = new Tone.Synth()

    synth.chain(
      autoFilter,
      autoPanner,
      autoWah,
      bitCrusher,
      chebyshev,
      chorus,
      convolver,
      distortion,
      feedbackDelay,
      feedbackEffect,
      freeverb,
      jcReverb,
      phaser,
      pingPongDelay,
      pitchShift,
      reverb,
      stereoWidener,
      tremolo,
      vibrato,
      Tone.Master
    )

    synth2.chain(
      autoFilter,
      autoPanner,
      autoWah,
      bitCrusher,
      chebyshev,
      chorus,
      convolver,
      distortion,
      feedbackDelay,
      feedbackEffect,
      freeverb,
      jcReverb,
      phaser,
      pingPongDelay,
      pitchShift,
      reverb,
      stereoWidener,
      tremolo,
      vibrato,
      Tone.Master
    )

    // let pattern = new Tone.Pattern(function(time, note) {
    //   synth.triggerAttackRelease(
    //     ['C6', 'C3', 'F4', 'A2', 'C1', 'F1'],
    //     '4n',
    //     time
    //   )
    // }, '4n')

    // let pattern = new Tone.Pattern(
    //   function(time, note) {
    //     synth.triggerAttackRelease(note, '4n')
    //   },
    //   ['C4', 'D4', 'E4']
    // )
    //
    // pattern.start(0)

    // let loop1 = new Tone.Loop(function(time) {
    //   synth2.triggerAttackRelease('C1', '1n', time)
    // }, '4n')

    let loop1 = new Tone.Loop(function(time) {
      ambientSynth.triggerAttackRelease('C2', '8n', time)
    }, '4n')

    let loop2 = new Tone.Loop(function(time) {
      ambientSynth.triggerAttackRelease('E2', '16n', time)
    }, '4n')

    let loop3 = new Tone.Loop(function(time) {
      leadSynth.triggerAttackRelease('C4', '1m', time)
    }, '1m')

    // let loop3 = new Tone.Loop(function(time) {
    //   synth.triggerAttackRelease('D2', '1n', time)
    // }, '4n')

    let loop4 = new Tone.Loop(function(time) {
      leadSynth.triggerAttackRelease('A1', '16n', time)
    }, '4n')

    ambientSynth.chain(
      // ambientAutoFilter,
      // ambientChorus,
      // ambientDistortion,
      // ambientFeedbackDelay,
      // ambientFreeverb,
      // ambientPhaser,
      // ambientPingPongDelay,
      autoFilter,
      autoPanner,
      autoWah,
      bitCrusher,
      chebyshev,
      chorus,
      convolver,
      distortion,
      feedbackDelay,
      feedbackEffect,
      freeverb,
      jcReverb,
      phaser,
      pingPongDelay,
      pitchShift,
      reverb,
      stereoWidener,
      tremolo,
      vibrato,
      Tone.Master
    )

    leadSynth.chain(
      autoFilter,
      autoPanner,
      autoWah,
      bitCrusher,
      chebyshev,
      chorus,
      convolver,
      distortion,
      feedbackDelay,
      feedbackEffect,
      freeverb,
      jcReverb,
      phaser,
      pingPongDelay,
      pitchShift,
      reverb,
      stereoWidener,
      tremolo,
      vibrato,
      Tone.Master
    )

    this.state = {
      ambientSynth,
      leadSynth,
      lastChange: Date.now(),
      timeout: 0,
      autoFilter: {
        effect: autoFilter,
        wet: 0,
        on: false
      },
      autoPanner: {
        effect: autoPanner,
        wet: 0,
        on: false
      },
      autoWah: {
        effect: autoWah,
        wet: 0,
        on: false
      },
      bitCrusher: {
        effect: bitCrusher,
        wet: 0,
        on: false
      },
      chebyshev: {
        effect: chebyshev,
        wet: 0,
        on: false
      },
      chorus: {
        effect: chorus,
        wet: 0,
        on: false
      },
      convolver: { convolver },
      distortion: {
        effect: distortion,
        wet: 0,
        on: false
      },
      feedbackDelay: {
        effect: feedbackDelay,
        wet: 0,
        on: false
      },
      feedbackEffect: {
        effect: feedbackEffect,
        wet: 0,
        on: false
      },
      freeverb: {
        effect: freeverb,
        wet: 0,
        on: false
      },
      jcReverb: {
        effect: jcReverb,
        wet: 0,
        on: false
      },
      phaser: {
        effect: phaser,
        wet: 0,
        on: false
      },
      pingPongDelay: { pingPongDelay },
      pitchShift: {
        effect: pitchShift,
        wet: 0,
        on: false
      },
      reverb: {
        effect: reverb,
        wet: 0,
        on: false
      },
      stereoWidener: {
        effect: stereoWidener,
        wet: 0,
        on: false
      },
      tremolo: {
        effect: tremolo,
        wet: 0,
        on: false
      },
      vibrato: {
        effect: vibrato,
        wet: 0,
        on: false
      },
      synth: {
        instrument: synth,
        on: false
      },
      synth2: {
        instrument: synth2,
        on: false
      },
      // pattern: {
      //   pattern: pattern,
      //   on: false
      // },
      loop1: {
        loop: loop1,
        on: false
      },
      loop2: {
        loop: loop2,
        on: false
      },
      loop3: {
        loop: loop3,
        on: false
      },
      loop4: {
        loop: loop4,
        on: false
      },

      part1: {
        part: parts.part1(leadSynth),
        on: false
      },
      part3: {
        part: parts.part3(leadSynth),
        on: false
      }
    }

    _.bindAll(
      this,
      'generateRandom',
      'getRandomArbitrary',
      'toggleLoop',
      'changeEffectWetValue',
      'changeDistortionValue',
      'togglEffect',
      'changeFeedbackDelayValue',
      'changeBitCrusherValue',
      'changeReverbValue',
      'changeTremoloFrequencyValue',
      'changeTremoloDepthValue',
      'changeFreeverbRoomSizeValue',
      'changeJcReverbValue',
      'changeReverbPreDelayValue',
      'changeSynthValue',
      'togglePart',
      'copyAll'
    )
  }

  changeSynthValue(synthName, effectName, value) {
    let synth = this.state[synthName]
    let envelope = synth.instrument.envelope
    envelope[effectName] = value

    this.setState({
      [`${effectName}`]: {
        oscillator: synth.instrument.oscillator,
        envelope: envelope
      }
    })
  }

  togglePart(partName) {
    let { part, on } = this.state[partName]

    if (on == true) {
      part.stop()
    } else {
      console.log('yo')
      part.at('1m')
      part.start(0)
      part.loop = true
      // part.loopEnd = '8m'
    }

    this.setState({
      [`${partName}`]: {
        part: part,
        on: !on
      }
    })

    Tone.Transport.bpm.value = 115
    Tone.Transport.start()
  }

  componentDidMount() {
    // this.generateRandom()

    let { effect, wet, on } = this.state.distortion

    console.log(this.props)

    effect.wet.value = on ? this.props.distortion.wet : 0
    effect.distortion = this.props.distortion.effect.distortion

    this.setState({
      distortion: {
        wet: this.props.distortion.wet,
        effect,
        on
      }
    })
  }

  copyAll() {
    const { distortion } = this.state
    const data = {
      distortion: {
        effect: {
          distortion: distortion.effect.distortion
        },
        wet: distortion.wet,
        on: distortion.on
      }
    }
    console.log(JSON.stringify(data))
  }

  getRandomArbitrary(min, max) {
    return Math.floor(Math.random() * (max - min)) + min
  }

  generateRandom() {
    const { lastChange, timeout } = this.state

    if (Date.now() - lastChange >= timeout) {
      const random = this.getRandomArbitrary(100, 3000)
      this.setState({
        lastChange: Date.now(),
        timeout: random
      })

      this.changeDistortionValue('distortion', random / 30)
    }

    setTimeout(() => this.generateRandom(), timeout)
  }

  toggleLoop(loopName) {
    let { loop, on } = this.state[loopName]

    on == true ? loop.stop() : loop.start('0m')

    this.setState({
      [`${loopName}`]: {
        loop: loop,
        on: !on
      }
    })

    Tone.Transport.bpm.value = 115
    Tone.Transport.start()
  }

  // togglePattern(patternName) {
  //   let { pattern, on } = this.state[patternName]
  //
  //   on == true ? pattern.stop() : pattern.start('0m')
  //
  //   this.setState({
  //     [`${patternName}`]: {
  //       pattern: pattern,
  //       on: !on
  //     }
  //   })
  // }

  togglEffect(effectName) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? 0 : wet
    on = !on

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeEffectWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? value : 0

    wet = value

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeDistortionValue(effectName, value) {
    let { effect, wet, on } = this.state.distortion

    effect.distortion = value

    this.setState({
      distortion: {
        effect,
        wet,
        on
      }
    })
  }

  changeFeedbackDelayValue(effectName, value) {
    let { effect, wet, on } = this.state.feedbackDelay

    effect.maxDelay = value

    this.setState({
