import React from 'react'

import PlaySwitch from '../PlaySwitch'
import ToggleSwitch from '../ToggleSwitch'
import Slider from '../Slider'
import Knob from '../Knob'

export default class Distortion extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      convolver: { convolver },
      distortion: {
        effect: distortion,
        wet: 0,
        on: false
      }
    }
  }

  render() {
    let name = 'distortion'
    const {
      effect,
      wet,
      on,
      changeEffectWetValue,
      changeDistortionValue,
      togglEffect
    } = this.props

    return (
      <div className="Effect green">
        <div className="Title">
          <h1>Distortion</h1>
        </div>
        <p>Wet</p>
        <Slider
          name={name}
          property="wet"
          min="0"
          max="1"
          value={wet}
          handleValueChange={changeEffectWetValue}
        />
        <p>Distortion</p>
        <Slider
          name={name}
          property="distortion"
          min="0"
          max="100"
          value={effect.distortion}
          handleValueChange={changeDistortionValue}
        />

        <PlaySwitch
          name="play"
          value={distortion.distortion}
          current={on}
          handleToggleClick={togglEffect}
        />
      </div>
    )
  }
}
