  togglePart(partName) {
    let { part, on } = this.state[partName]

    if (on == true) {
      part.stop()
    } else {
      part.start(0)
      console.log('yo')
      // part.at('1m')
      part.loop = true
      // part.loopEnd = '8m'
    }

    this.setState({
      [`${partName}`]: {
        part: part,
        on: !on
      }
    })

    Tone.Transport.bpm.value = 115
    Tone.Transport.start()
  }

  toggleSeq(seqName) {
    let { sequence, on } = this.state[seqName]

    if (on == true) {
      sequence.stop()
    } else {
      sequence.start(0)
      console.log('yo')
      sequence.loop = true
    }

    this.setState({
      [`${seqName}`]: {
        sequence: sequence,
        on: !on
      }
    })

    Tone.Transport.bpm.value = 115
    Tone.Transport.start()
  }

  toggleNote1(seqName) {
    let { sequence, addNotes, on, id } = this.state[seqName]
    sequence.id = 1

    if (addNotes == true) {
      sequence.remove(1)
      sequence.add(1, ['C3', 'C3'])
    } else {
      sequence.remove(1)
      sequence.add(1, [['G3', 'G3', 'G3'], ['Eb4', 'Eb4']])
    }
    addNotes = !addNotes

    this.setState({
      [`${seqName}`]: {
        sequence,
        on,
        addNotes,
        id
      }
    })
  }

