          />
          <p>add notes 1</p>
          <PlaySwitch
            name="play"
            id={sequence1.id}
            value={sequence1.addNotes}
            handleToggleClick={() => this.toggleNote1('sequence1')}
          />
          <p>add notes 2</p>
          <PlaySwitch
            name="play"
            id={sequence1.id}
            value={sequence1.addNotes}
            handleToggleClick={() => this.toggleNote2('sequence1')}
          />
          <p>add notes 3</p>
          <PlaySwitch
