import Tone from 'tone'

function part1(synth) {
  let part = new Tone.Part(
    function(time, note) {
      synth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    },
    [
      {
        time: '0:0:0',
        noteName: 'C3',
        velocity: 1,
        duration: '2n'
      },
      {
        time: '1:0:0',
        noteName: 'E3',
        velocity: 1,
        duration: '2n'
      },
      {
        time: '2:0:0',
        noteName: 'A2',
        velocity: 1,
        duration: '2n'
      },
      {
        time: '3:0:0',
        noteName: 'G2',
        velocity: 1,
        duration: '2n'
      },
      {
        time: '4:0:0',
        noteName: 'E2',
        velocity: 1,
        duration: '2n'
      },
      {
        time: '5:0:0',
        noteName: 'F2',
        velocity: 1,
        duration: '2n'
      },
      {
        time: '6:0:0',
        noteName: 'A2',
        velocity: 1,
        duration: '2n'
      },
      {
        time: '7:0:0',
        noteName: 'D3',
        velocity: 1,
        duration: '2n'
      }
    ]
  )

  return part
}

function part2(synth) {
  let part = new Tone.Part(
    function(time, note) {
      synth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    },
    [
      {
        time: '0:0:0',
        noteName: 'C3',
        velocity: 1,
        duration: '32n'
      },
      {
        time: '0:1:0',
        noteName: 'E3',
        velocity: 1,
        duration: '32n'
      },
      {
        time: '0:2:0',
        noteName: 'A2',
        velocity: 1,
        duration: '32n'
      },
      {
        time: '0:3:0',
        noteName: 'G2',
        velocity: 1,
        duration: '32n'
      },
      {
        time: '1:0:0',
        noteName: 'E2',
        velocity: 1,
        duration: '32n'
      },
      {
        time: '1:1:0',
        noteName: 'F2',
        velocity: 1,
        duration: '32n'
      },
      {
        time: '1:2:0',
        noteName: 'A2',
        velocity: 1,
        duration: '32n'
      },
      {
        time: '1:3:0',
        noteName: 'D3',
        velocity: 1,
        duration: '32n'
      }
    ]
  )

  return part
}

function part3(synth) {
  var part = new Tone.Part(
    function(time, note) {
      //the notes given as the second element in the array
      //will be passed in as the second argument
      synth.triggerAttackRelease(note, '8n', time)
    },
    [[0, 'C2'], ['0:1', 'C3'], ['0:1:2', 'C3'], ['0:2', 'C4'], ['0:3', 'C5']]
  ).start()

  part.loop = true

  setTimeout(function() {
    part.remove('0:1')
    part.add('0:1', 'Eb3')
  }, 4000)

  setTimeout(function() {
    part.remove('0:1')
    part.remove('0:1:2')
    part.add('0:1', 'Eb3')
    part.add('0:1:1', 'E3')
    part.add('0:1:2', 'F3')
    part.add('0:1:3', 'F#3')
  }, 8000)

  setTimeout(function() {
    part.remove('0:1')
    part.remove('0:1:1')
    part.remove('0:1:2')
    part.remove('0:1:3')
    part.add('0:1', 'C3')
    part.add('0:1:2', 'C3')
  }, 12000)

  return part
}

