import React from "react";
export default class Avatar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "red"
    };

    this.handleRedClick = this.handleClick.bind(this);
    this.handleBlueClick = this.handleClick.bind(this);
  }

  handleBlueClick() {
    console.log("Inner handle click");
    this.props.handleClick();

    this.setState({ color: "blue" });
  }

  handleRedClick() {
    console.log("Inner handle click");
    this.props.handleClick();

    this.setState({ color: "red" });
  }

  // handleClick() {
  //   console.log("Inner handle click");
  //   this.props.handleClick();
  //
  //   this.setState({
  //     color: "blue"
  //   });
  // }

  render() {
    const { name, handleClick } = this.props;

    const color = this.state.color;

    if (color) {
      a = (
        <div
          onClick={this.handleBlueClick}
          style={{ color: this.state.color }}
        />
      );
    } else {
      a = (
        <div
          onClick={this.handleRedClick}
          style={{ color: this.state.color }}
        />
      );
    }

    return (
      <div onClick={this.handleClick} style={{ color: this.state.color }}>
        {name} {this.state.color}
      </div>
    );
  }
}
